import React, { useState } from "react";
import {
  FaBars,
  FaDollarSign,
  FaBuffer,
  FaBuilding,
  FaTh,
  FaBookOpen,
} from "react-icons/fa";
import { NavLink } from "react-router-dom";

const Slidebar = ({ children }) => {
  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);
  const menuItem = [
    {
      path: "/",
      name: "Dashboard",
      icon: <FaTh />,
    },
    {
      path: "/academics",
      name: "Academics",
      icon: <FaBookOpen />,
    },
    {
      path: "/examination",
      name: "Examination",
      icon: <FaBuffer />,
    },
    {
      path: "/finance",
      name: "Finance",
      icon: <FaDollarSign />,
    },
    {
      path: "/hostel",
      name: "Hostel",
      icon: <FaBuilding />,
    },
  ];
  return (
    <div className="container">
      <div style={{ width: isOpen ? "300px" : "50px" }} className="sidebar">
        <div className="top_section">
          <h1 style={{ width: isOpen ? "300px" : "50px" }} className="log0">
            CUH <br /> Student Portal
          </h1>
          <div className="bars">
            <FaBars onClick={toggle} />
          </div>
        </div>
        {menuItem.map((item, index) => (
          <NavLink
            to={item.path}
            key={index}
            className="link"
            activeclassName="active"
          >
            <div className="icon">{item.icon}</div>
            <div className="link_text">{item.name}</div>
          </NavLink>
        ))}
      </div>
      <main>{children}</main>
    </div>
  );
};

export default Slidebar;
