import React, { Children } from "react";
import {
  FaBars,
  FaCommentAlt,
  FaRegChartBar,
  FaShoppingBag,
  FaUserAlt,
} from "react-icons/fa";
import { NavLink } from "react-router-dom";

const Slidebar = ({ Children }) => {
  const menuItem = [
    {
      path: "/",
      name: "Dashboard",
      icon: <Fath />,
    },
    {
      path: "/academics",
      name: "Academics",
      icon: <FaUserAlt />,
    },
    {
      path: "/examination",
      name: "Examination",
      icon: <FaRegChartBar />,
    },
    {
      path: "/finance",
      name: "Finance",
      icon: <FaCommentAlt />,
    },
    {
      path: "/hostel",
      name: "Hostel",
      icon: <FaShoppingBag />,
    },
  ];
  return (
    <div className="container">
      <div className="sidebar">
        <div className="top_section">
          <h1 className="log0">
            CUH <br /> Student Portal
          </h1>
          <div className="bars">
            <FaBars />
          </div>
        </div>
        {menuItem.map((item, index) => (
          <NavLink
            to={item.path}
            key={index}
            className="link"
            activeclassName="active"
          >
            <div className="icon">{item.icon}</div>
            <div className="link_text">{item.name}</div>
          </NavLink>
        ))}
      </div>
      <main>{Children}</main>
    </div>
  );
};

export default Slidebar;
